INSERT INTO `oc_modification` (`modification_id`,`extension_install_id`,`name`,`code`,`author`,`version`,`link`,`xml`,`status`,`date_added`) VALUES (58,67,'Pavo Store Locator','pavstorelocator','pavothemes','1.0.0','http://www.pavothemes.com','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n\t<name>Pavo Store Locator</name>\n\t<code>pavstorelocator</code>\n\t<version>1.0.0</version>\n\t<author>pavothemes</author>\n\t<link>http://www.pavothemes.com</link>\n\t<file path=\"admin/view/template/localisation/location_form.twig\">\n        <operation>\n            <search><![CDATA[ </form>]]></search>\n            <add position=\"before\"><![CDATA[\n\t\t\t\t\t     <div class=\"form-group\">\n\t\t\t\t            <label class=\"col-sm-2 control-label\" for=\"input-email\">{{ entry_email }}</label>\n\t\t\t\t            <div class=\"col-sm-10\">\n\t\t\t\t              <input type=\"text\" name=\"email\" value=\"{{ email }}\" placeholder=\"{{ entry_email }}\" id=\"input-email\" class=\"form-control\" />\n\t\t\t\t            </div>\n\t\t\t\t          </div>   \n\t\t\t\t          <div class=\"form-group\">\n\t\t\t\t            <label class=\"col-sm-2 control-label\" for=\"input-video\">{{ entry_video }}</label>\n\t\t\t\t            <div class=\"col-sm-10\">\n\t\t\t\t              <input type=\"text\" name=\"video\" value=\"{{ video }}\" placeholder=\"{{ entry_video }}\" id=\"input-video\" class=\"form-control\" />\n\t\t\t\t            </div>\n\t\t\t\t          </div>   \n\n\t\t\t\t             <div class=\"table-responsive form-group\">\n\t\t\t\t               <label class=\"col-sm-2 control-label\" for=\"input-gallery\">{{ entry_gallery }}</label>\n\t\t\t\t                <div class=\"col-sm-10\">\n\t\t\t\t                  <table id=\"images\" class=\"table table-striped table-bordered table-hover\">\n\t\t\t\t                    <thead>\n\t\t\t\t                      <tr>\n\t\t\t\t                        <td class=\"text-left\">{{ entry_additional_image }}</td>\n\t\t\t\t                        <td class=\"text-right\">{{ entry_sort_order }}</td>\n\t\t\t\t                        <td></td>\n\t\t\t\t                      </tr>\n\t\t\t\t                    </thead>\n\t\t\t\t                    <tbody>\n\t\t\t\t                    \n\t\t\t\t                    {% set image_row = 0 %}\n\t\t\t\t                    {% for image in images %}\n\t\t\t\t                    <tr id=\"image-row{{ image_row }}\">\n\t\t\t\t                      <td class=\"text-left\"><a href=\"\" id=\"thumb-image{{ image_row }}\" data-toggle=\"image\" class=\"img-thumbnail\"><img src=\"{{ catalog_url }}/image/{{ images[image_row] }}\" alt=\"\" title=\"\" data-placeholder=\"{{ placeholder }}\" /></a>\n\t\t\t\t                        <input type=\"hidden\" name=\"images[{{ image_row }}]\" value=\"{{ images[image_row] }}\" id=\"input-image{{ image_row }}\" /></td>\n\t\t\t\t                   \n\t\t\t\t                      <td class=\"text-left\"><button type=\"button\" onclick=\"$(\'#image-row{{ image_row }}\').remove();\" data-toggle=\"tooltip\" title=\"{{ button_remove }}\" class=\"btn btn-danger\"><i class=\"fa fa-minus-circle\"></i></button></td>\n\t\t\t\t                    </tr>\n\t\t\t\t                    {% set image_row = image_row + 1 %}\n\t\t\t\t                    {% endfor %}\n\t\t\t\t                      </tbody>\n\t\t\t\t                    \n\t\t\t\t                    <tfoot>\n\t\t\t\t                      <tr>\n\t\t\t\t                        <td colspan=\"2\"></td>\n\t\t\t\t                        <td class=\"text-left\"><button type=\"button\" onclick=\"addImage();\" data-toggle=\"tooltip\" title=\"{{ button_image_add }}\" class=\"btn btn-primary\"><i class=\"fa fa-plus-circle\"></i></button></td>\n\t\t\t\t                      </tr>\n\t\t\t\t                    </tfoot>\n\t\t\t\t                  </table>\n\t\t\t\t                </div>\n\t\t\t\t              </div>\n\n\t\t\t\t           <script type=\"text/javascript\">\n\t\t\t\t            var image_row = {{ image_row }};\n\n\t\t\t\t            function addImage() {\n\t\t\t\t              html  = \'<tr id=\"image-row\' + image_row + \'\">\';\n\t\t\t\t              html += \'  <td class=\"text-left\"><a href=\"\" id=\"thumb-image\' + image_row + \'\"data-toggle=\"image\" class=\"img-thumbnail\"><img src=\"{{ placeholder }}\" alt=\"\" title=\"\" data-placeholder=\"{{ placeholder }}\" /></a><input type=\"hidden\" name=\"images[\' + image_row + \']\" value=\"\" id=\"input-image\' + image_row + \'\" /></td>\';\n\t\t\t\t             \n\t\t\t\t              html += \'  <td class=\"text-left\"><button type=\"button\" onclick=\"$(\\\'#image-row\' + image_row  + \'\\\').remove();\" data-toggle=\"tooltip\" title=\"{{ button_remove }}\" class=\"btn btn-danger\"><i class=\"fa fa-minus-circle\"></i></button></td>\';\n\t\t\t\t              html += \'</tr>\';\n\n\t\t\t\t              $(\'#images tbody\').append(html);\n\n\t\t\t\t              image_row++;\n\t\t\t\t            }\n\t\t\t\t            </script> \n            ]]></add>\n        </operation>\n    </file>\n     \n     <file path=\"admin/controller/localisation/location.php\">\n\n        <operation>\n            <search><![CDATA[$this->model_localisation_location->addLocation($this->request->post); ]]></search>\n            <add position=\"replace\">\n            \t<![CDATA[ $id = $this->model_localisation_location->addLocation($this->request->post);\n            \t\t\t $this->load->model(\'extension/pavstorelocator/location\');\n\t\t\t\t\t\t $this->model_extension_pavstorelocator_location->edit( $id, $this->request->post );\n            \t]]>\n            </add>\n        </operation>    \n        <operation>\n            <search><![CDATA[ \t$this->load->model(\'tool/image\'); ]]></search>\n            <add position=\"before\"><![CDATA[\n\t\t\t\tif (isset($this->request->post[\'video\'])) {\n\t\t\t\t\t$data[\'video\'] = $this->request->post[\'video\'];\n\t\t\t\t} elseif (!empty($location_info)) {\n\t\t\t\t\t$data[\'video\'] = $location_info[\'video\'];\n\t\t\t\t} else {\n\t\t\t\t\t$data[\'video\'] = \'\';\n\t\t\t\t}\n\n\t\t\t\tif (isset($this->request->post[\'email\'])) {\n\t\t\t\t\t$data[\'email\'] = $this->request->post[\'email\'];\n\t\t\t\t} elseif (!empty($location_info)) {\n\t\t\t\t\t$data[\'email\'] = $location_info[\'email\'];\n\t\t\t\t} else {\n\t\t\t\t\t$data[\'email\'] = \'\';\n\t\t\t\t}\n\n\n\t\t\t\t$data[\'catalog_url\'] = HTTP_CATALOG;\n\t\t\t\tif (isset($this->request->post[\'email\'])) {\n\t\t\t\t\t$data[\'images\'] = $this->request->post[\'images\'];\n\t\t\t\t} elseif (!empty($location_info)) {\n\t\t\t\t\t$data[\'images\'] = explode( \"|\", $location_info[\'gallery\'] );\n\t\t\t\t} else {\n\t\t\t\t\t$data[\'images\'] = \'\';\n\t\t\t\t}\n            ]]></add>\n        </operation>\n        <operation>\n            <search><![CDATA[ $this->model_localisation_location->editLocation($this->request->get[\'location_id\'], $this->request->post); ]]></search>\n            <add position=\"after\"><![CDATA[\n            \t$this->load->model(\'extension/pavstorelocator/location\');\n\t\t\t\t$this->model_extension_pavstorelocator_location->edit( $this->request->get[\'location_id\'], $this->request->post );\n            ]]></add>\n        </operation>    \n    </file>\n\n      <file path=\"admin/language/*/localisation/location.php\">\n     \t\n        <operation>\n            <search><![CDATA[$_[\'entry_name\']       = \'Store Name\'; ]]></search>\n            <add position=\"replace\">\n            \t<![CDATA[\n            \t\t$_[\'entry_name\']        = \'Location name\';\n            \t\t$_[\'entry_email\']       = \'Email\';\n            \t\t$_[\'entry_video\']       = \'Video\';\n            \t\t$_[\'entry_gallery\']     = \'Gallery\';\n            \t]]>\n            </add>\n        </operation>    \n    </file>    \n</modification>',1,'2018-01-17 09:25:24.000');
