INSERT INTO `oc_modification` (`modification_id`,`extension_install_id`,`name`,`code`,`author`,`version`,`link`,`xml`,`status`,`date_added`) VALUES (37,46,'Gerenciar Blog','pavoblog','pavothemes','1.0.0','http://www.pavothemes.com','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n\t<name>Gerenciar Blog</name>\n\t<code>pavoblog</code>\n\t<version>1.0.0</version>\n\t<author>pavothemes</author>\n\t<link>http://www.pavothemes.com</link>\n    <file path=\"admin/controller/common/column_left.php\">\n        <operation>\n            <search><![CDATA[// Catalog]]></search>\n            <add position=\"before\"><![CDATA[\n                // pavoblog language file\n                $this->load->language(\'extension/module/pavoblog\');\n\n                // PavoConfigurações do Blog  menu\n                $settings = array();\n\n                // access posts\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/posts\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_posts_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/index\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                // modify posts\n                if ( $this->user->hasPermission( \'modify\', \'extension/module/pavoblog/post\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_post_add_new_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/post\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                // access categories\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/categories\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_categories_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/categories\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                // access comments\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/comments\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_comments_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/comments\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n                // view settings panel\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoblog/settings\' ) ) {\n                    $settings[] = array(\n                        \'name\'     => $this->language->get(\'menu_settings_text\'),\n                        \'href\'     => $this->url->link(\'extension/module/pavoblog/settings\', \'user_token=\' . $this->session->data[\'user_token\'], true),\n                        \'children\' => array()\n                    );\n                }\n\n                if ( $settings ) {\n                    $data[\'menus\'][] = array(\n                        \'id\'       => \'pavoblog\',\n                        \'icon\'     => \'fa fa-tasks fw\',\n                        \'name\'     => $this->language->get( \'heading_title\' ),\n                        \'href\'     => \'\',\n                        \'children\' => $settings\n                    );\n                }\n                // End PavoConfigurações do Blog  menu\n            ]]></add>\n        </operation>\n    </file>\n    <file path=\"catalog/controller/common/header.php\">\n        <operation>\n            <search><![CDATA[$data[\'title\'] = $this->document->getTitle();]]></search>\n            <add position=\"before\"><![CDATA[\n        if ( $this->config->get( \'pavoblog_default_style\' ) ){\n            $file = DIR_TEMPLATE . \'default/stylesheet/pavoblog.min.css\';\n            if ( file_exists( $file ) ) {\n                $file = str_replace( DIR_APPLICATION, basename( DIR_APPLICATION ) . \'/\', $file );\n                $this->document->addStyle( $file );\n            }\n        }]]></add>\n        </operation>\n    </file>\n    <file path=\"system/engine/loader.php\">\n        <operation>\n            <search>\n                <![CDATA[$template = new Template($this->registry->get(\'config\')->get(\'template_engine\'));]]>\n            </search>\n            <add position=\"replace\">\n                <![CDATA[$template = new Template($this->registry->get(\'config\')->get(\'template_engine\'), $this->registry->get(\'config\')->get(\'config_theme\'));]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"system/library/template.php\">\n        <operation>\n            <search>\n                <![CDATA[public function __construct($adaptor) {]]>\n            </search>\n            <add position=\"replace\">\n                <![CDATA[public function __construct($adaptor, $theme = \'default\' ){\n                    $this->theme = $theme;\n                ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n                <![CDATA[$this->adaptor = new $class();]]>\n            </search>\n            <add position=\"replace\">\n                <![CDATA[$this->adaptor = new $class( $this->theme );]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"system/library/template/twig.php\">\n        <operation>\n            <search>\n                <![CDATA[public function __construct() {]]>\n            </search>\n            <add position=\"replace\"><![CDATA[public function __construct( $theme = \'default\' ) {\n                    $this->theme = $theme;\n                ]]></add>\n        </operation>\n        <operation>\n            <search>\n                <![CDATA[$config = array(\'autoescape\' => false);]]>\n            </search>\n            <add position=\"before\">\n            <![CDATA[\n                if ( ! defined( \'DIR_CATALOG\' ) ) {\n                    $dirs = array(\n                        DIR_TEMPLATE . $this->theme . \'/template/\',\n                        DIR_TEMPLATE . \'default/template/\'\n                    );\n                    foreach ( $dirs as $dir ) {\n                        if ( is_dir( $dir ) ) {\n                            $loader->addPath( $dir );\n                        }\n                    }\n                }\n                ]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"catalog/controller/startup/seo_url.php\">\n        <operation>\n            <search>\n                <![CDATA[$this->request->get[\'route\'] = $query->row[\'query\'];]]>\n            </search>\n            <add position=\"replace\"><![CDATA[\n                    if ( $url[0] === \'pavo_cat_id\' ) {\n                        // archive\n                        $this->request->get[\'route\'] = \'extension/pavoblog/archive\';\n                        $this->request->get[\'pavo_cat_id\'] = $url[1];\n                    } else if ( $url[0] === \'pavo_post_id\' ) {\n                        // single\n                        $this->request->get[\'route\'] = \'extension/pavoblog/single\';\n                        $this->request->get[\'pavo_post_id\'] = $url[1];\n                    } else if ( $url[0] === \'extension/pavoblog/archive/author\' && ! empty( $parts[1] ) ) {\n                        $author = $parts[1];\n                        $query = $this->db->query( \"SELECT * FROM \" . DB_PREFIX . \"user WHERE username = \'\".$this->db->escape( $author ).\"\'\" );\n                        $user = $query->row;\n                        if ( ! empty( $user[\'user_id\'] ) ) {\n                            $this->request->get[\'route\'] = \'extension/pavoblog/archive\';\n                            $this->request->get[\'pavo_username\'] = $user[\'username\'];\n                        }\n                        break;\n                    } else {\n                        $this->request->get[\'route\'] = $query->row[\'query\'];\n                    }\n                ]]></add>\n        </operation>\n        <operation>\n            <search>\n                <![CDATA[} elseif ($key == \'path\') {]]>\n            </search>\n            <add position=\"replace\"><![CDATA[\n                    } else if ( strpos( $data[\'route\'], \'extension/pavoblog/\' ) === 0 ) {\n\n                        if ( $data[\'route\'] === \'extension/pavoblog/archive\' ) {\n                            if ( $key === \'route\' ) {\n                                $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape($value) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                                if ( $query->num_rows && ! empty( $query->row[\'keyword\'] ) ) {\n                                    $url .= \'/\' . $query->row[\'keyword\'];\n                                    unset($data[$key]);\n                                }\n                                if ( isset( $data[\'pavo_cat_id\'] ) ) {\n                                    $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape(\'pavo_cat_id=\' . (int)$data[\'pavo_cat_id\']) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                                    if ( $query->num_rows && ! empty( $query->row[\'keyword\'] ) ) {\n                                        $url .= \'/\' . $query->row[\'keyword\'];\n                                        unset($data[\'pavo_cat_id\']);\n                                    }\n                                }\n                            }\n                        } else if ( $data[\'route\'] === \'extension/pavoblog/archive/author\' ) {\n\n                            if ( $key === \'pavo_username\' ) {\n                                $query = $this->db->query( \"SELECT * FROM \" . DB_PREFIX . \"user WHERE username = \'\".$this->db->escape( $value ).\"\'\" );\n                                $user = $query->row;\n                                if ( ! empty( $user[\'user_id\'] ) ) {\n                                    $url .= \'/\' . $user[\'username\'];\n                                }\n                                unset($data[$key]);\n                            } else if ( $key === \'route\' ) {\n                                $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape($value) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                                if ( $query->num_rows && ! empty( $query->row[\'keyword\'] ) ) {\n                                    $url .= \'/\' . $query->row[\'keyword\'];\n                                }\n                            }\n                        } else if ( $data[\'route\'] === \'extension/pavoblog/single\' && $key === \'pavo_post_id\' ) {\n                            $query = $this->db->query(\"SELECT * FROM \" . DB_PREFIX . \"seo_url WHERE `query` = \'\" . $this->db->escape($key . \'=\' . (int)$value) . \"\' AND store_id = \'\" . (int)$this->config->get(\'config_store_id\') . \"\' AND language_id = \'\" . (int)$this->config->get(\'config_language_id\') . \"\'\");\n                            if ( $query->num_rows && $query->row[\'keyword\'] ) {\n                                $url .= \'/\' . $query->row[\'keyword\'];\n                                unset($data[$key]);\n                            }\n                        }\n                    } elseif ($key == \'path\') {\n                ]]></add>\n        </operation>\n    </file>\n</modification>',1,'2017-11-16 09:45:06.000');
INSERT INTO `oc_modification` (`modification_id`,`extension_install_id`,`name`,`code`,`author`,`version`,`link`,`xml`,`status`,`date_added`) VALUES (80,90,'Pavo Megamenu','pavmegamenu','pavothemes','1.0.0','http://www.pavothemes.com','<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n\t<name>Pavo Megamenu</name>\r\n\t<code>pavmegamenu</code>\r\n\t<version>1.0.0</version>\r\n\t<author>pavothemes</author>\r\n\t<link>http://www.pavothemes.com</link>\r\n   \r\n    <file path=\"admin/controller/common/column_left.php\">\r\n        <operation>\r\n            <search><![CDATA[// Catalog]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                // pavmegamenu language file\r\n                $this->load->language(\'extension/module/pavmegamenu\');\r\n\r\n                // pavmegamenu Settings menu\r\n                $settings = array();\r\n                // view settings panel\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavmegamenu\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'text_megamenu_menus\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavmegamenu\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                if ( $settings ) {\r\n                    $data[\'menus\'][] = array(\r\n                        \'id\'       => \'pavmegamenu\',\r\n                        \'icon\'     => \'fa fa-bars\',\r\n                        \'name\'     => $this->language->get( \'text_megamenu_menus\' ),\r\n                        \'href\'     => \'\',\r\n                        \'children\' => $settings\r\n                    );\r\n                }\r\n                // End pavmegamenu Settings menu\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    \r\n</modification>',1,'2018-03-23 09:13:21.000');
INSERT INTO `oc_modification` (`modification_id`,`extension_install_id`,`name`,`code`,`author`,`version`,`link`,`xml`,`status`,`date_added`) VALUES (43,52,'Navegação em Camadas','pavlayerednavigation','pavothemes','1.0.0','http://www.pavothemes.com','<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<modification>\n\t<name>Navegação em Camadas</name>\n\t<code>pavlayerednavigation</code>\n\t<version>1.0.0</version>\n\t<author>pavothemes</author>\n\t<link>http://www.pavothemes.com</link>\n\n    <file path=\"catalog/model/catalog/product.php\">\n        <operation>\n            <search><![CDATA[if (!empty($data[\'filter_manufacturer_id\'])) {]]></search>\n            <add position=\"before\">\n                <![CDATA[\n        /* Price range */\n        if  (!empty($data[\'filter_price\'])) {\n            $min_price = $data[\'filter_price\'][\'min_price\'];\n            $max_price = $data[\'filter_price\'][\'max_price\'];\n            $sql_filter_special = \"(SELECT price FROM \" . DB_PREFIX . \"product_special ps WHERE ps.product_id = p.product_id AND ps.customer_group_id = \'\" . (int)$this->config->get(\'config_customer_group_id\') . \"\' AND ((ps.date_start = \'0000-00-00\' OR ps.date_start < NOW()) AND (ps.date_end = \'0000-00-00\' OR ps.date_end > NOW())) ORDER BY ps.priority ASC, ps.price ASC LIMIT 1)\";\n            $sql_sl_discount = \"(SELECT price FROM \" . DB_PREFIX . \"product_discount pd2 WHERE pd2.product_id = p.product_id AND pd2.customer_group_id = \'\" . (int)$this->config->get(\'config_customer_group_id\') . \"\' AND pd2.quantity = \'1\' AND ((pd2.date_start = \'0000-00-00\' OR pd2.date_start < NOW()) AND (pd2.date_end = \'0000-00-00\' OR pd2.date_end > NOW())) ORDER BY pd2.priority ASC, pd2.price ASC LIMIT 1)\";\n            $sql .= \" AND (CASE WHEN \" . $sql_filter_special . \" IS NOT NULL THEN \" . $sql_filter_special . \" WHEN \" . $sql_sl_discount . \" IS NOT NULL THEN \". $sql_sl_discount . \" ELSE p.price END) >=\'\". $min_price .\"\'\" ;\n            $sql .= \" AND (CASE WHEN \" . $sql_filter_special . \" IS NOT NULL THEN \" . $sql_filter_special . \" WHEN \" . $sql_sl_discount . \" IS NOT NULL THEN \". $sql_sl_discount . \" ELSE p.price END) <=\'\". $max_price .\"\'\";\n        }\n        /* End */\n        if (!empty($data[\'filter_manufacturers\']) && is_array($data[\'filter_manufacturers\']) ) {\n            $sql .= \" AND p.manufacturer_id IN (\" . implode(\",\", $data[\'filter_manufacturers\']) . \")\";\n        }\n      \n                ]]>\n            </add>\n        </operation>\n    </file>\n    <file path=\"catalog/controller/product/category.php\">\n        <operation>\n            <search><![CDATA[$product_total = $this->model_catalog_product->getTotalProducts($filter_data);]]></search>\n            <add position=\"before\">\n                <![CDATA[   \n                \n                    $filter_price = array();\n                    $rate = (float) $this->currency->getValue($this->session->data[\'currency\']);\n                    if (isset($this->request->get[\'price\'])) {\n                        $price_data = explode(\',\', $this->request->get[\'price\'] );\n                        if( count($price_data) == 2  ){\n                            $filter_price[\'min_price\'] = ceil($price_data[0] / $rate - 1);\n                            $filter_price[\'max_price\'] = round($price_data[1] / $rate);\n                        }\n            \n                    }   \n                    $manufacturers = array();\n                    if( isset($this->request->get[\'manufacturer\']) && $this->request->get[\'manufacturer\'] ){\n         \n                        $manufacturers = explode( \",\", $this->request->get[\'manufacturer\'] );\n                        foreach ($manufacturers as $key => $value) {\n                            $manufacturers[$key] = (int)$value;\n                        }\n                    }\n                    $filter_data[\'filter_price\'] = $filter_price;\n                    $filter_data[\'filter_manufacturers\'] = $manufacturers;\n                ]]>\n            </add>    \n        </operation>  \n    </file>\n        \n    <file path=\"catalog/view/theme/*/template/product/category.twig\">\n        <operation>\n            <search><![CDATA[{% if products %}]]></search>\n            <add position=\"before\">\n                <![CDATA[\n                <div class=\"products-collection\">\n            ]]>\n            </add>\n        </operation>  \n         <operation>\n            <search><![CDATA[ {% if not categories and not products %}]]></search>\n            <add position=\"before\">\n                <![CDATA[\n               </div>\n            ]]>\n            </add>\n        </operation>    \n    </file>\n</modification>',1,'2017-11-29 14:25:14.000');
