INSERT INTO `oc_modification` (`modification_id`,`extension_install_id`,`name`,`code`,`author`,`version`,`link`,`xml`,`status`,`date_added`) VALUES (84,94,'PavoThemer','pavothemer','pavothemes','1.0.0','http://www.pavothemes.com','<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n\t<name>PavoThemer</name>\r\n\t<code>pavothemer</code>\r\n\t<version>1.0.0</version>\r\n\t<author>pavothemes</author>\r\n\t<link>http://www.pavothemes.com</link>\r\n    <file path=\"admin/controller/user/user_permission.php\">\r\n        <operation>\r\n            <search><![CDATA[$data[\'permissions\'] = array();]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                $data[\'permissions\'] = array(\r\n                    \'extension/module/pavothemer/edit\',\r\n                    \'extension/module/pavothemer/customize\',\r\n                    \'extension/module/pavothemer/tools\',\r\n                    \'extension/module/pavothemer/management\',\r\n                    \'extension/module/pavoblog/posts\',\r\n                    \'extension/module/pavoblog/post\',\r\n                    \'extension/module/pavoblog/category\',\r\n                    \'extension/module/pavoblog/categories\',\r\n                    \'extension/module/pavoblog/comments\',\r\n                    \'extension/module/pavoblog/comment\',\r\n                    \'extension/module/pavoblog/settings\'\r\n                );\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"admin/controller/common/column_left.php\">\r\n        <operation>\r\n            <search><![CDATA[// Catalog]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                // pavothemer language file\r\n                $this->load->language(\'extension/module/pavothemer\');\r\n\r\n                // PavoThemer Settings menu\r\n                $settings = array();\r\n                // view settings panel\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavothemer/edit\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'menu_settings_text\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavothemer/edit\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n                $settings[] = array(\r\n                    \'name\'     => $this->language->get(\'menu_theme_text\'),\r\n                    \'href\'     => $this->url->link(\'extension/theme/\'.$this->config->get(\'config_theme\'), \'store_id=0&user_token=\' . $this->session->data[\'user_token\'], true),\r\n                    \'children\' => array()\r\n                );\r\n\r\n                // access customize\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavothemer/customize\' ) ) {\r\n                    $settings[] = array(\r\n                       \'name\'     => $this->language->get(\'menu_customize_text\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavothemer/customize\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                // view settings panel\r\n                $theme = $this->config->get( \'config_theme\' );\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavothemer/tools\' ) || $this->user->hasPermission( \'access\', \'extension/module/pavothemer/tools\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'menu_tool_text\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavothemer/tools\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                // theme management\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavothemer/management\' ) || $this->user->hasPermission( \'access\', \'extension/module/pavothemer/management\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'menu_management_text\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavothemer/management\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                // view homebuilder\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavobuilder\' ) || $this->user->hasPermission( \'access\', \'extension/module/pavobuilder\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'menu_builder_text\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavobuilder\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                // view header builder\r\n                if ( $this->user->hasPermission( \'access\', \'extension/module/pavoheader\' ) || $this->user->hasPermission( \'access\', \'extension/module/pavoheader\' ) ) {\r\n                    $settings[] = array(\r\n                        \'name\'     => $this->language->get(\'menu_header_builder_text\'),\r\n                        \'href\'     => $this->url->link(\'extension/module/pavoheader\', \'user_token=\' . $this->session->data[\'user_token\'], true),\r\n                        \'children\' => array()\r\n                    );\r\n                }\r\n\r\n                if ( $settings ) {\r\n                    $data[\'menus\'][] = array(\r\n                        \'id\'       => \'pavothemer\',\r\n                        \'icon\'     => \'fa fa-cog fw\',\r\n                        \'name\'     => $this->language->get( \'heading_title\' ),\r\n                        \'href\'     => \'\',\r\n                        \'children\' => $settings\r\n                    );\r\n                }\r\n                // End PavoThemer Settings menu\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"catalog/controller/common/header.php\">\r\n        <operation>\r\n            <search><![CDATA[$data[\'title\'] = $this->document->getTitle();]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n            $data[\'pavothemer\'] = $this->load->controller(\'extension/module/pavothemer\');\r\n            $this->load->model( \'extension/module/pavothemer\' );\r\n            $data[\'pavmegamenu\'] = $this->load->controller(\'extension/module/pavmegamenu\');\r\n            $data[\'pavverticalmegamenu\'] = $this->load->controller(\'extension/module/pavverticalmenu\');\r\n            $data[\'pavoheader\'] = $this->model_extension_module_pavothemer->getHeader();\r\n            $data[\'offcanvas\']  = $this->model_extension_module_pavothemer->getConfig( \'offcanvas\' );\r\n       ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"catalog/controller/common/header.php\">\r\n        <operation>\r\n            <search><![CDATA[return $this->load->view(\'common/header\', $data);]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n            if( empty($data[\'pavoheader\']) ) {    \r\n                $header_layout  = $this->model_extension_module_pavothemer->getConfig( \'header_layout\' );\r\n                if( $header_layout ){\r\n                    return $this->load->view(\'common/\'.trim($header_layout), $data);\r\n                }\r\n            }\r\n\r\n       ]]></add>\r\n        </operation>\r\n\r\n        <operation>\r\n            <search><![CDATA[return $this->load->view(\'common/header\', $data);]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n        $data[\'body_class\'] = $this->getBodyClass();\r\n        // pavothemer header layout file\r\n        $data[\'styles\'] = $this->model_extension_module_pavothemer->compressCss( $data[\'styles\'] );\r\n        $data[\'scripts\'] = $this->model_extension_module_pavothemer->compressJs( $data[\'scripts\'] );\r\n\r\n        return $this->load->view(\'common/header\', $data);\r\n    }\r\n\r\n    /**\r\n     * Get body class\r\n     */\r\n    public function getBodyClass() {\r\n        $this->load->model( \'extension/module/pavothemer\' );\r\n        $route = ! empty( $this->request->get[\'route\'] ) ? $this->request->get[\'route\'] : \'common/home\';\r\n\r\n        $class = \'\';\r\n        if( $this->config->get( \'pavothemer_keepheader\' ) ){\r\n            $class = \' has-header-sticky\';\r\n        }\r\n        $class .= $this->model_extension_module_pavothemer->getBodyClass();\r\n        return \'page-\' . implode( \'-\', explode( \'/\', $route ) ) . $class;\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"catalog/controller/common/footer.php\">\r\n        <operation>\r\n            <search><![CDATA[return $this->load->view(\'common/footer\', $data);]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                // pavothemer footer layout file\r\n                $this->load->model( \'extension/module/pavothemer\' );\r\n                $data[\'blockbuilder\'] = $this->model_extension_module_pavothemer->getFooter();\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n     <file path=\"system/engine/loader.php\">\r\n        <operation>\r\n            <search>\r\n                <![CDATA[foreach ($data as $key => $value) {]]>\r\n            </search>\r\n            <add position=\"before\">\r\n                <![CDATA[$template->set( \'product_grid_layout\', $this->registry->get(\'config\')->get( \'pavothemer_product_grid_layout\' ) );]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"catalog/controller/product/compare.php\">\r\n        <operation>\r\n            <search><![CDATA[$this->response->redirect($this->url->link(\'product/compare\'));]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                if (!empty($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && strtolower($_SERVER[\'HTTP_X_REQUESTED_WITH\']) == \'xmlhttprequest\') {\r\n                    $results = array();\r\n                    $results[\'status\'] = true;\r\n                    $results[\'success\'] = $this->session->data[\'success\'];\r\n                    $results[\'total\'] = count($this->session->data[\'compare\']);\r\n                    $results[\'text_compare\'] = sprintf( $this->language->get(\'text_compare\'), $results[\'total\'] );\r\n                    unset($this->session->data[\'success\']);\r\n                    echo json_encode($results); die();\r\n                } else {\r\n                    $this->response->redirect($this->url->link(\'product/compare\'));\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n\r\n    <file path=\"catalog/controller/product/product.php\">\r\n        <operation>\r\n            <search><![CDATA[$this->response->setOutput($this->load->view(\'product/product\', $data));]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                // pavothemer product layout file\r\n\r\n                $this->load->model( \'extension/module/pavothemer\' );\r\n                $layout = $this->model_extension_module_pavothemer->getConfig( \'product_layout\' );\r\n                $data[\'preview\'] =  $this->model_extension_module_pavothemer->getConfig( \'preview\' );\r\n                $data[\'preview\'] .= \" \" .  $this->model_extension_module_pavothemer->getConfig( \'zoommode\' );\r\n                if ( ! $layout ) {\r\n                    $this->response->setOutput($this->load->view(\'product/product\', $data));\r\n                } else {\r\n                    $this->response->setOutput($this->load->view(\'product/\' . $layout, $data));\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$data[\'products\'][] = array(]]></search>\r\n            <add position=\"before\">\r\n                <![CDATA[\r\n                    $output = array();\r\n                    $images = $this->model_catalog_product->getProductImages( $result[\'product_id\'] );\r\n                    if( $images ){\r\n                        foreach( $images as $timage ){\r\n                            if ($timage[\'image\']) {\r\n                                $tmp = $this->model_tool_image->resize($timage[\'image\'], $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));\r\n                            } else {\r\n                                $tmp = $this->model_tool_image->resize(\'placeholder.png\', $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\' . $this->config->get(\'config_theme\') . \'_image_product_height\'));\r\n                            }\r\n                            $output[] = $tmp;\r\n                        }\r\n                    }\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$data[\'products\'][] = array(]]></search>\r\n            <add position=\"after\">\r\n                <![CDATA[\r\n                    \'images\'      => $output,\r\n                ]]>\r\n            </add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"catalog/controller/product/search.php\">