INSERT INTO `oc_product_description` (`product_id`,`language_id`,`name`,`description`,`tag`,`meta_title`,`meta_description`,`meta_keyword`) VALUES (48,2,'Stacked Skinny  Destroyed Stitch','&lt;div class=&quot;cpt_product_description &quot;&gt;\r\n\t&lt;div&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\t&lt;strong&gt;More room to move.&lt;/strong&gt;&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\tWith 80GB or 160GB of storage and up to 40 hours of battery life, the new iPod classic lets you enjoy up to 40,000 songs or up to 200 hours of video or any combination wherever you go.&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\t&lt;strong&gt;Cover Flow.&lt;/strong&gt;&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\tBrowse through your music collection by flipping through album art. Select an album to turn it over and see the track list.&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\t&lt;strong&gt;Enhanced interface.&lt;/strong&gt;&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\tExperience a whole new way to browse and view your music and video.&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\t&lt;strong&gt;Sleeker design.&lt;/strong&gt;&lt;/p&gt;\r\n\t\t&lt;p&gt;\r\n\t\t\tBeautiful, durable, and sleeker than ever, iPod classic now features an anodized aluminum and polished stainless steel enclosure with rounded edges.&lt;/p&gt;\r\n\t&lt;/div&gt;\r\n&lt;/div&gt;\r\n&lt;!-- cpt_container_end --&gt;','','Stacked Skinny  Destroyed Stitch','','');
INSERT INTO `oc_product_description` (`product_id`,`language_id`,`name`,`description`,`tag`,`meta_title`,`meta_description`,`meta_keyword`) VALUES (28,1,'Off-White Arad Turtleneck','&lt;p&gt;&lt;b&gt;BRAND&lt;/b&gt;&lt;br&gt;\r\nDelivering seasonal trends for the high street, Warehouse offer a collection of directional pieces, with vibrant prints and clean cut tailoring channelling &lt;br&gt;\r\nthe brand\'s signature style. Statement party dresses sit alongside classic wardrobe staples, all complemented by a key range of jewellery and accessories,&lt;br&gt;\r\n  including an exclusive edit of three satchel bags.&lt;/p&gt;\r\n&lt;p&gt;&lt;b&gt;SIZE &amp;amp; FIT&lt;/b&gt;&lt;br&gt;\r\nModel wears: UK 8/ EU 34/ US 4&lt;br&gt;\r\n  Model\'s height: 173 cm/5\'8”&lt;/p&gt;\r\n&lt;p&gt;&lt;b&gt;LOOK AFTER ME&lt;/b&gt;&lt;br&gt;\r\nExpert TLC necessary&lt;br&gt;\r\n  Drop it off at the dry cleaners&lt;/p&gt;\r\n&lt;p&gt;&lt;b&gt;\tABOUT ME&lt;/b&gt;&lt;br&gt;\r\nMid-weight faux leather &lt;br&gt;\r\nLooks just as good as the real deal&lt;br&gt;\r\n&lt;br&gt;\r\nBacking: 100% Polyester, Main: 100% Polyurethane, Trim: 100% Polyester.&lt;/p&gt;','','Off-White Arad Turtleneck','','');
